import React, { useEffect } from "react";
import { getDetail,getCountries } from "../actions";
import { useDispatch,useSelector } from "react-redux";
import { Link } from "react-router-dom";




export default function Detail(props){
    console.log(props)
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getCountries())
    },[dispatch])

    useEffect(()=>{
        dispatch(getDetail(props.match.params.id))
    },[dispatch])

    const allDetails = useSelector((state)=>state.detail);



    return (
        <div>
            {
                allDetails.length > 0 && <div>
                    <h3>{allDetails.name}</h3>
                    <img src={allDetails.image} alt="img not found" width="200px" height="250px"/>
                    <h5>{allDetails.id}</h5>
                    <h5>{allDetails.continent}</h5>
                    <h5>{allDetails.capital}</h5>
                    <h5>{allDetails.subregion}</h5>
                    <h5>{allDetails.area}</h5>
                    <h5>{allDetails.population}</h5>
                    <h5>{allDetails.TouristActivities}</h5>
                </div>
            }
        </div>
    )
}
