import React,{useState} from "react";
import { Link,useHistory } from "react-router-dom";
import { postTouristActivity } from "../actions";
import {useDispatch,useSelector} from "react-redux";

import styles from "../estilos/TouristActivity.module.css"



function validate (input){
    let errors = {};
    if(input.name===""){
        errors.name = "Se requiere un nombre"
    } else if(input.countriesid===""){
        errors.countriesid = "Se requiere un id"
    } else if ( input.difficulty > 5 && input.difficulty < 0){
        errors.difficulty = "Ingrese un dato numerico entre 0 y 5"
    } else if (input.duration.length>2){
        errors.duration = "Ingrese un dato en horas"
    }
    return errors
}


export default function TouristActivityCreated(){
    const dispatch = useDispatch()
    const history = useHistory()
    const [errors,setErrors]= useState({})
    const allcountries = useSelector(state=>state.countries)
    const[countries,setCountries]=useState("")
    const countrie = allcountries.map(el=>el.name)
    const [input,setInput] = useState({
        name:"",
        countriesid:"",
        difficulty:"",
        duration:"",
        temporada:""

    })


    function handleChange(e){
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
        setErrors(validate({
            ...input,
            [e.target.name]:e.target.value
        }))
    }

    function handleCheck(e){
        if(e.target.checked){
            setInput({
                ...input,
                temporada:[...input.temporada, e.target.value]
            })
        }
    }

    function handleSelectID(e){

    }



    function handleSubmit(e){
        e.preventDefault();
        console.log(input)
        dispatch(postTouristActivity(input))
        alert("Tourist Activity created!!")
        setInput({
            name:"",
            countriesid:"",
            difficulty:"",
            duration:"",
            temporada:""
        })
        history.push("/home")

    }

    return (
        <div className={styles.container}>
            <Link to= "/home"><button className={styles.back}>Back to home</button></Link>
            <h1>actividad turistica</h1>
            <form onSubmit={(e)=> handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={input.name} name="name" onChange={(e)=>handleChange(e)}/>
                    {errors.name &&(<p>{errors.name}</p>)}
                </div>
                <div>
                    <label>Countrie id:</label>
                    <input type="text" value={input.countriesid} name="countriesid" onChange={(e)=>handleChange(e)}/>
                    {errors.countriesid &&(<p>{errors.countriesid}</p>)}
                </div>
                <select onChange={(e)=>handleSelectID(e)}>
                            <option value={countrie}>smaller population</option>
                        </select>
                <div>
                    <label>Difficulty:</label>
                    <input type="number" name="difficulty" value={input.difficulty} onChange={(e)=>handleChange(e)}/>
                    {errors.difficulty &&(<p>{errors.difficulty}</p>)}
                </div>
                <div>
                    <label>Duration:</label>
                    <input type="number" name="duration" value={input.duration} onChange={(e)=>handleChange(e)}/>
                    {errors.duration &&(<p>{errors.duration}</p>)}
                </div>
                <div>
                        <label>Temporada:</label>

                        <label>
                        <input type="checkbox" name="Spring" value="Spring" onChange={(e)=>handleCheck(e)}/>
                        Spring
                        </label>

                        <label>
                        <input type="checkbox" name="Summer" value="Summer" onChange={(e)=>handleCheck(e)}/>
                        Summer
                        </label>

                        <label>
                        <input type="checkbox" name="Fall" value="Fall" onChange={(e)=>handleCheck(e)}/>
                        Fall
                        </label>

                        <label>
                        <input type="checkbox" name="Winter" value="Winter" onChange={(e)=>handleCheck(e)}/>
                        Winter
                        </label>
                </div>
                <div>
                    <button className={styles.create} type="submit">Create Tourist Activity</button>
                </div>
            </form>
        </div>
    )

}
