import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector} from "react-redux";
import { getCountries } from "../actions";
import Card from "../components/Card"
import {Link} from "react-router-dom"
export default function Home (){
    const dispatch = useDispatch();
    const allCountries = useSelector((state)=>state.countries)

    useEffect(()=>{
        dispatch(getCountries())
    },[dispatch])

    function handleClick(e){
        e.preventDefault();
        dispatch(getCountries())
    }
    function handleSortName(e){
        e.preventDefault();
        dispatch(getCountries())
    }
    function handleSortPopulation(e){
        e.preventDefault();
        dispatch(getCountries())
    }
    function handleFilterContinent(e){
        e.preventDefault();
        dispatch(getCountries())
    }

    return (
        <div>
            <Link to= "/TouristActivity">Create Tourist Activity</Link>
            <h1>Hola soy tu home</h1>
            <button onClick={(e)=>{handleClick(e)}}>Countries</button>
            <div>
                <select onChange={e => handleSortName(e)}>
                    <option value="Asc">Upward</option>
                    <option value="Desc">Falling</option>
                </select>
                <select onChange={e => handleSortPopulation(e)}>
                    <option value="Asc">Population Upward</option>
                    <option value="Desc">Population Falling</option>
                </select>
                <select onChange={e => handleFilterContinent(e)}>
                    <option value="Oceania">Oceania</option>
                    <option value="Asia">Asia</option>
                    <option value="Africa">Africa</option>
                    <option value="Europe">Europe</option>
                    <option value="Americas">Americas</option>
                    <option value="Antarctic">Antarctic</option>
                </select>

                {
                    allCountries && allCountries.map(el =>{
                        return(
                            <Card name={el.name} image={el.image} continent={el.continent} key={el.id}/>
                        )

                    })
                }
            </div>
        </div>
    )
}
