import React,{useState,useEffect} from "react";
import { Link,useHistory } from "react-router-dom";
import { getGenres,postTouristActivity } from "../actions";
import {useDispatch,useSelector} from "react-redux";

export default function TouristActivityCreated(){
    const dispatch = useDispatch()
    const history = useHistory()
    const genres = useSelector((state)=>state.genres)

    const [input,setInput] = useState({
        name:"",
        difficulty:"",
        duration:"",
        temporada:""
    })


    function handleChange(e){
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
        console.log(input)
    }

    function handleCheck(e){
        if(e.target.checked){
            setInput({
                ...input,
                platform:e.target.value
            })
        }
    }

    function handleSelect(e){
        setInput({
            ...input,
            genre:[...input.genre, e.target.value]
        })
    }

    function handleSubmit(e){
        e.preventDefault();
        console.log(input)
        dispatch(postVideogames(input))
        // alert("Video game created!!")
        setInput({
            name:"",
            image:"",
            description:"",
            releaseDate:"",
            rating:"",
            platform:"",
            genre:[]
        })
        history.push("/home")

    }
    useEffect(()=>{
        dispatch(getGenres());
    },[]);

    return (
        <div>
            <Link to= "/home"><button>Return</button></Link>
            <h1>Create your video game</h1>
            <form onSubmit={(e)=> handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={input.name} name="name" onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                    <label>Image:</label>
                    <input type="text" name="image" value={input.image} onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                    <label>Description:</label>
                    <input type="text" name="description" value={input.description} onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                    <label>ReleaseDate:</label>
                    <input type="text" name="releaseDate" value={input.releaseDate} onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                    <label>Rating:</label>
                    <input type="number" name="rating" value={input.rating} onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                        <label>Platform:</label>
                        <label>
                        <input type="checkbox" name="PlayStation" value="PlayStation" onChange={(e)=>handleCheck(e)}/>
                        PlayStation
                        </label>

                        <label>
                        <input type="checkbox" name="Pc" value="Pc" onChange={(e)=>handleCheck(e)}/>
                        Pc
                        </label>

                        <label>
                        <input type="checkbox" name="Xbox" value="Xbox" onChange={(e)=>handleCheck(e)}/>
                        Xbox
                        </label>
                </div>
                <div>
                    <select onChange={(e)=> handleSelect(e)}>
                        {genres.map((el)=>(
                            <option value={el.name}>{el.name}</option>
                        ))}
                    </select>
                        {/* <ul><li>{input.genre.map(el=> el + ",")}</li></ul> */}
                    <button type="submit">Create Video game</button>
                </div>
            </form>
        </div>
    )

}
