import React,{useState} from "react";
import { Link,useHistory } from "react-router-dom";
import { postTouristActivity } from "../actions";
import {useDispatch} from "react-redux";

export default function TouristActivityCreated(){
    const dispatch = useDispatch()
    const history = useHistory()

    const [input,setInput] = useState({
        name:"",
        difficulty:"",
        duration:"",
        temporada:"",
        countriesid:""
    })


    function handleChange(e){
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
        console.log(input)
    }

    function handleCheck(e){
        if(e.target.checked){
            setInput({
                ...input,
                temporada:[...input.temporada, e.target.value]
            })
        }
    }

    function handleSelect(e){
        setInput({
            ...input,
            countriesid: e.target.value
        })
    }



    function handleSubmit(e){
        e.preventDefault();
        console.log(input)
        dispatch(postTouristActivity(input))
        // alert("Video game created!!")
        setInput({
            name:"",
            difficulty:"",
            duration:"",
            temporada:"",
            countriesid:""
        })
        history.push("/home")

    }

    return (
        <div>
            <Link to= "/home"><button>Return</button></Link>
            <h1>Create your video game</h1>
            <form onSubmit={(e)=> handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={input.name} name="name" onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                    <label>Difficulty:</label>
                    <input type="number" name="difficulty" value={input.difficulty} onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                    <label>Duration:</label>
                    <input type="number" name="duration" value={input.duration} onChange={(e)=>handleChange(e)}/>
                </div>
                <div>
                        <label>Temporada:</label>

                        <label>
                        <input type="checkbox" name="Spring" value="Spring" onChange={(e)=>handleCheck(e)}/>
                        Spring
                        </label>

                        <label>
                        <input type="checkbox" name="Summer" value="Summer" onChange={(e)=>handleCheck(e)}/>
                        Summer
                        </label>

                        <label>
                        <input type="checkbox" name="Fall" value="Fall" onChange={(e)=>handleCheck(e)}/>
                        Fall
                        </label>

                        <label>
                        <input type="checkbox" name="Winter" value="Winter" onChange={(e)=>handleCheck(e)}/>
                        Winter
                        </label>
                </div>
                <div>
                <div>
                    <select onChange={(e)=> handleSelect(e)}>
                        {genres.map((el)=>(
                            <option value={el.name}>{el.name}</option>
                        ))}
                    </select>
                        {/* <ul><li>{input.genre.map(el=> el + ",")}</li></ul> */}
                    <button type="submit">Create Video game</button>
                </div>
                    <button type="submit">Create Video game</button>
                </div>
            </form>
        </div>
    )

}
