import React, { useEffect } from "react";
import { getDetail } from "../actions";
import { useDispatch,useSelector } from "react-redux";
import { Link,useParams } from "react-router-dom";

import styles from "../estilos/Detail.module.css"




export default function Detail(){

    const dispatch = useDispatch();
    const {id}=useParams()

    useEffect(()=>{
        dispatch(getDetail(id))
    },[id])


    const allDetails = useSelector((state)=>state.detail);
    console.log(allDetails)

    return (
        <div>
            {
                allDetails.length > 0
                    ? <div>
                        <h1>{allDetails[0].id}</h1>
                        <h1>{allDetails[0].name}</h1>
                        <img className={Styles.image} src={allDetails[0].image}/>
                        <h1>{allDetails[0].subregion}</h1>  
                        <h1>{allDetails[0].temporada}</h1>
                        <h1>{allDetails[0].population}</h1>
                        <h1>{allDetails[0].continent}</h1>
                        <h1>{allDetails[0].TouristActivities.map(el => el.name + " ")}</h1>                        
                    </div>
                    : <p>Loading...</p>
            }
            <Link to="/Home">
                <button>Back to Home</button>
            </Link>
        </div>
    )

}
